<template>
    <img v-lazy="lazyOpt" @click="onClick">
</template>
<script>
import Vue from 'vue';
import VueLazyload from 'vue-lazyload';

const defaultImg = {
    icon: 'data:image/png;base64,',
    pic: 'data:image/gif;base64,',
};

// 注册
Vue.use(VueLazyload);

export default {
    props: {
        src: { type: String },
        type: { type: String, default: 'pic' },
        loading: { type: String },
        error: { type: String },
    },
    data() {
        return {
            lazyOpt: {
                src: this.src,
                loading: this.loading || this.getDefaultSrc(),
                error: this.error || this.getDefaultSrc(),
            }
        };
    },
    mounted() {
    },
    methods: {
        onClick() { this.$emit('click') },
        getDefaultSrc() {
            return defaultImg[this.type] || defaultImg['pic'];
        }
    },

    components: {
    },
};
</script>